mapper.select:
select count(*) as total from ( select t5.*, t2.ANNOUNCE_PROPER_NUM as t5_ANNOUNCE_PROPER_NUM, CONVERT(t5.aplicn_dtls_date, CHAR) AS aplicn_dtls_date_as_varchar, t2.announce_title from TB_005 as t5 left join TB_002 as t2 on t5.ANNOUNCE_PROPER_NUM = t2.ANNOUNCE_PROPER_NUM) as result where user_proper_num = #{user_proper_num}
and announce_title like concat('%', #{cri.announce_title}, '%')
and aplicn_dtls_sts like concat('%', #{cri.aplicn_dtls_sts}, '%')
and aplicn_dtls_date >= #{cri.aplicn_dtls_startdate}
and aplicn_dtls_date <= #{cri.aplicn_dtls_enddate}

select result.*, CONVERT(result.aplicn_dtls_date, CHAR) AS aplicn_dtls_date_as_varchar from ( select t5.*, t2.announce_title from TB_005 as t5 left join TB_002 as t2 on t5.ANNOUNCE_PROPER_NUM = t2.ANNOUNCE_PROPER_NUM ) as result where user_proper_num = #{user_proper_num}
and announce_title like concat('%', #{cri.announce_title}, '%')
and aplicn_dtls_sts like concat('%', #{cri.aplicn_dtls_sts}, '%')
and aplicn_dtls_date >= #{cri.aplicn_dtls_startdate}
and aplicn_dtls_date <= #{cri.aplicn_dtls_enddate}
order by result.aplcn_dtls_proper_num desc
limit #{cri.pageStart}, #{cri.amount}

select t2.announce_title, t5.aplicn_dtls_sts, t5.aplicn_dtls_date, t5.ligtn_case_carer_yn, t5.ligtn_case_carer_etc, t5.insrn_indst_carer_yn, t5.insrn_indst_carer_etc, t5.criminal_penalty_carer_yn, t5.criminal_penalty_carer_etc
from TB_005 as t5 left join TB_002 as t2 on t5.ANNOUNCE_PROPER_NUM = t2.ANNOUNCE_PROPER_NUM
where user_proper_num = #{user_proper_num} and aplcn_dtls_proper_num = #{aplcn_dtls_proper_num}

select t6.edctn_school_name, t6.edctn_major, t6.edctn_admsn_date, t6.edctn_grdtn_date, t6.edctn_degree
from TB_005 as t5 left join TB_006 as t6 on t5.aplcn_dtls_proper_num = t6.aplcn_dtls_proper_num
where t5.user_proper_num = #{user_proper_num} and t5.aplcn_dtls_proper_num = #{aplcn_dtls_proper_num}

select t7.company_name, t7.work_department, t7.work_position, t7.work_start_date, t7.work_end_date
from TB_005 as t5 left join TB_007 as t7 on t5.aplcn_dtls_proper_num = t7.aplcn_dtls_proper_num
where t5.user_proper_num = #{user_proper_num} and t5.aplcn_dtls_proper_num = #{aplcn_dtls_proper_num}

select t8.crtfc_number, t8.issue_agency, t8.issue_date
from TB_005 as t5 left join TB_008 as t8 on t5.aplcn_dtls_proper_num = t8.aplcn_dtls_proper_num
where t5.user_proper_num = #{user_proper_num} and t5.aplcn_dtls_proper_num = #{aplcn_dtls_proper_num}

select t9.file_type, t9.file_code, t9.original_file_name
from TB_005 as t5 left join TB_009 as t9 on t5.aplcn_dtls_proper_num = t9.aplcn_dtls_proper_num
where t5.user_proper_num = #{user_proper_num} and t5.aplcn_dtls_proper_num = #{aplcn_dtls_proper_num}

select court_name
from TB_005 as t5 left join TB_011 as t11 on t5.court_proper_num = t11.court_proper_num
where user_proper_num = #{user_proper_num} and aplcn_dtls_proper_num = #{aplcn_dtls_proper_num}

select count(*) as total
from TB_012
where user_proper_num = #{user_proper_num}

	and judgement_num like concat('%', #{cri.judgement_num},
	'%')


	and act_complete_yn like concat('%', #{cri.act_complete_yn},
	'%')


	and chosen_date >= #{cri.chosen_startdate}


	and chosen_date <= #{cri.chosen_enddate}


	and attendance_date >= #{cri.attendance_startdate}


	and attendance_date <= #{cri.attendance_enddate}

select *,
CONVERT(chosen_date, CHAR) AS chosen_date_as_varchar,
CONVERT(attendance_date, CHAR) AS attendance_date_as_varchar
from
TB_012
where user_proper_num =
#{user_proper_num}


	and judgement_num like concat('%', #{cri.judgement_num},
	'%')


	and act_complete_yn like concat('%', #{cri.act_complete_yn},
	'%')


	and chosen_date >= #{cri.chosen_startdate}


	and chosen_date <= #{cri.chosen_enddate}


	and attendance_date >= #{cri.attendance_startdate}


	and attendance_date <= #{cri.attendance_enddate}


order by act_proper_num desc

limit #{cri.pageStart},
#{cri.amount}

select *, CONVERT(accept_date, CHAR) AS accept_date_as_varchar from TB_014 where user_proper_num = #{user_proper_num}
order by accept_proper_num desc
limit #{cri.pageStart}, #{cri.amount}

select *
from TB_014
where user_proper_num =
#{user_proper_num}
and accept_proper_num = #{accept_proper_num}

mapper.update:
update TB_014
set accept_act_yn = 'N'
where
accept_proper_num = #{accept_proper_num}
and user_proper_num =
#{user_proper_num}

update TB_014
set accept_act_yn = 'Y'
where
accept_proper_num = #{accept_proper_num}
and user_proper_num =
#{user_proper_num}
