mapper.select:
		select trial_fcltt_proper_num
		from TB_010
		where 		

            trial_fcltt_clasifi_code = #{trial_fcltt_clasifi_code}


            trial_fcltt_sbcls_code = #{trial_fcltt_sbcls_code}

select count(*) as total from TB_002 where 1 = 1
and announce_title like concat('%', #{cri.announce_title}, '%')
and announce_content like concat('%', #{cri.announce_content}, '%' )
and announce_start_date >= #{cri.announce_start_date}
and announce_end_date <= #{cri.announce_end_date}
order by announce_proper_num desc

select TB_002.*, CONVERT(announce_start_date, CHAR) AS announce_start_date_as_varchar, CONVERT(announce_end_date, CHAR) AS announce_end_date_as_varchar from TB_002 where 1 = 1
and announce_title like concat('%', #{cri.announce_title}, '%')
and announce_content like concat('%', #{cri.announce_content}, '%')
and announce_start_date >= #{cri.announce_start_date}
and announce_end_date <= #{cri.announce_end_date}
order by announce_proper_num desc limit #{cri.pageStart}, #{cri.amount}
